mapper.select:
select id,rnum,title,rdate,fname,imp,grpno,indent, ansnum, r FROM( select id,rnum,title,rdate,fname,imp,grpno,indent, ansnum, rownum r FROM( select id,rnum,title,rdate,fname,imp,grpno,indent,ansnum from rbbs order by rnum desc, ansnum
wname like '%' || #{word}||'%'
title like '%' || #{title}||'%'
content like '%' || #{content} || '%'
))
where r>= #{sno} and r<=#{eno}

    	SELECT COUNT(rnum) as cnt       
		FROM rbbs                           
		WHERE rnum= #{rnum} AND id = #{id}
