mapper.update:
update pbbs
set content = #{content},
bname = #{bname}
where bnum = #{bnum}

mapper.select:
 	SELECT bnum, bname, content, bdate, passwd, r
 		from(
 			select bnum, bname, content, bdate, passwd, rownum r
 			from (
 				select bnum, bname, content, to_char(bdate, 'yyyy-mm-dd')bdate, passwd
 				from pbbs
 	
	
		
		bname like '%' || #{word} || '%'
		
		
		content like '%' || #{word} || '%'
		
	
	   
	order by bnum DESC            
		)
	)
	
	where r>=#{sno} and r<=#{eno}

select count(*) from pbbs
where bnum = #{bnum}
and passwd = #{passwd}
